class Polynomial:
    def __init__(self, coefficients):
        # coefficients is a list where index i is the coefficient of x^i
        self.coefficients = coefficients

    def __call__(self, x):
        # Evaluate the polynomial for a given x value
        result = 0
        for i, coeff in enumerate(self.coefficients):
            result += coeff * (x ** i)
        return result

    def __str__(self):
        terms = [f'{c}x^{i}' for i, c in enumerate(self.coefficients) if c != 0]
        return ' + '.join(terms[::-1])

