print(f"Inorder Traversal (Sorted): {list(bst.inorder_traversal(bst.root))}")

class Stack:
    def __init__(self):
        self.items = []

    def is_empty(self):
        return not self.items

    def push(self, item):
        self.items.append(item)

    def pop(self):
        if not self.is_empty():
            return self.items.pop()
        else:
            raise IndexError("pop from empty stack")

    def peek(self):
        if not self.is_empty():
            return self.items[-1]
        else:
            return None

def check_parentheses_balance(expression):
    s = Stack()
    mapping = {')': '(', ']': '[', '}': '{'}
    for char in expression:
        if char in '({[':
            s.push(char)
        elif char in ')]}':
            if s.is_empty() or s.pop() != mapping[char]:
                return False
    return s.is_empty()

print(f"'({[()]})' is balanced: {check_parentheses_balance('({[()]})')}")
print(f"'([)]' is balanced: {check_parentheses_balance('([)]')}")

def find_kth_smallest_quickselect(arr, k):
    # Implementation of the Quickselect algorithm (similar to Quicksort)
    if not arr:
        return None

    pivot = arr[len(arr) // 2]
    less = [x for x in arr if x < pivot]
    equal = [x for x in arr if x == pivot]
    greater = [x for x in arr if x > pivot]

    # k is 1-based index for the problem (1st smallest, 2nd smallest, etc.)
    if k <= len(less):
        # Kth element must be in the 'less' partition
        return find_kth_smallest_quickselect(less, k)
    elif k <= len(less) + len(equal):
        # Kth element is the pivot itself
        return equal[0]
    else:
        # Kth element must be in the 'greater' partition
        # Adjust k by subtracting the size of the 'less' and 'equal' partitions
        return find_kth_smallest_quickselect(greater, k - len(less) - len(equal))

