const bubbleSort = (arr) => {
  let n = arr.length;
  let swapped;
  do {
    swapped = false;
    for (let i = 0; i < n - 1; i++) {
      if (arr[i] > arr[i + 1]) {
        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]]; // ES6 swap
        swapped = true;
      }
    }
    n--;
  } while (swapped);
  return arr;
};

