const recursiveFactorial = (n) => {
    if (n < 0) {
        throw new Error('Factorial of negative number is undefined');
    }
    // Base case: factorial of 0 or 1 is 1
    if (n <= 1) {
        return 1;
    }
    // Recursive step
    return n * recursiveFactorial(n - 1);
};

const memoizedFactorial = (n, cache = {}) => {
    if (n in cache) {
        return cache[n];
    }
    if (n <= 1) {
        return 1;
    }
    cache[n] = n * memoizedFactorial(n - 1, cache);
    return cache[n];
};

