const checkJWTFormat = (token) => {
    if (!token) return false;
    const parts = token.split('.');
    if (parts.length !== 3) return false; 

    // Simple base64url validation (not full JWT validation)
    const base64UrlRegex = /^[A-Za-z0-9-_]+\.[A-Za-z0-9-_]+\.[A-Za-z0-9-_]+$/;
    if (!base64UrlRegex.test(token)) return false;

    // Function to safely decode a base64url part
    const safeDecode = (part) => {
        try {
            return atob(part.replace(/-/g, '+').replace(/_/g, '/'));
        } catch (e) {
            return null;
        }
    };

    const header = safeDecode(parts[0]);
    const payload = safeDecode(parts[1]);

    if (header && payload) {
        console.log(`JWT Header: ${header}`);
        console.log(`JWT Payload: ${payload}`);
        return true;
    }
    return false;
};

