        // Function composition: f(g(x))
        Function<Integer, Integer> addTwo = x -> x + 2;
        Function<Integer, Integer> multiplyByThree = x -> x * 3;

        // compose: multiplyByThree(addTwo(x)) -> (x + 2) * 3
        Function<Integer, Integer> composed = multiplyByThree.compose(addTwo);

        // andThen: addTwo(multiplyByThree(x)) -> (x * 3) + 2
