import java.util.Arrays;
import java.util.List;
import java.util.Collections;

public class BinarySearchIterative {
    public static int binarySearch(List<Integer> sortedList, int key) {
        int low = 0;
        int high = sortedList.size() - 1;

        while (low <= high) {
            int mid = low + (high - low) / 2; // Prevents overflow
            int midVal = sortedList.get(mid);

            if (midVal < key) {
                low = mid + 1; // Search right half
            } else if (midVal > key) {
                high = mid - 1; // Search left half
            } else {
                return mid; // Key found
            }
        }
        return -1; // Key not found
    }

    public static void main(String[] args) {
        List<Integer> data = Arrays.asList(2, 5, 8, 12, 16, 23, 38, 56, 72, 91);
        System.out.println("Index of 23: " + binarySearch(data, 23));
    }
}

