import java.util.concurrent.CompletableFuture;
import java.util.concurrent.ExecutionException;

public class CompletableFutureExample {
    public static void main(String[] args) throws ExecutionException, InterruptedException {
        // 1. Supply a value asynchronously
        CompletableFuture<String> future = CompletableFuture.supplyAsync(() -> {
            try {
                Thread.sleep(500); // Simulate long-running task
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
            return "Typr_ Data";
        });

        // 2. Process the result when it becomes available
        CompletableFuture<String> processedFuture = future.thenApply(result -> {
            System.out.println("Processing: " + result);
            return result.toUpperCase();
        });

        // 3. Consume the final result
        CompletableFuture<Void> finalFuture = processedFuture.thenAccept(finalResult -> {
            System.out.println("Final Result: " + finalResult);
        });

        // Wait for the final future to complete
        finalFuture.get();
        System.out.println("Asynchronous process finished.");
    }
}

public class CustomListImplementation {
    private int[] data;
    private int size = 0;
    private static final int DEFAULT_CAPACITY = 10;

    public CustomListImplementation() {
        data = new int[DEFAULT_CAPACITY];
    }

    public void add(int element) {
        if (size == data.length) {
            // Grow the array (simplified resizing: double the capacity)
            int newCapacity = data.length * 2;
            data = java.util.Arrays.copyOf(data, newCapacity);
        }
        data[size++] = element;
    }

    public int get(int index) {
        if (index < 0 || index >= size) {
            throw new IndexOutOfBoundsException("Index: " + index + ", Size: " + size);
        }
        return data[index];
    }

    public int size() { return size; }

    public static void main(String[] args) {
        CustomListImplementation list = new CustomListImplementation();
        list.add(10);
        list.add(20);
        System.out.println("List size: " + list.size());
        System.out.println("Element at index 1: " + list.get(1));
    }
}

import java.util.Map;
import java.util.HashMap;
import java.util.Objects;

class Key {
    private final String name;
    private final int id;

    public Key(String name, int id) {
        this.name = name;
        this.id = id;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Key key = (Key) o;
        return id == key.id && Objects.equals(name, key.name);
    }

    @Override
    public int hashCode() {
        return Objects.hash(name, id);
    }
}

public class CustomKeyExample {
    public static void main(String[] args) {
        Map<Key, String> data = new HashMap<>();
        Key key1 = new Key("A", 1);
        Key key2 = new Key("A", 1); // Logically the same key

        data.put(key1, "Value 1");

        // Because equals() and hashCode() are implemented, key1 and key2 are considered the same key
        System.out.println("Key 1 hash: " + key1.hashCode());
        System.out.println("Key 2 hash: " + key2.hashCode());
        System.out.println("Value for key 2: " + data.get(key2));
    }
}

import java.util.Arrays;
import java.util.PriorityQueue;

public class DijkstraAlgorithmBasic {
    // Simplified implementation for a directed weighted graph represented by an adjacency matrix (less idiomatic, but simple for snippet)
    private static final int INF = Integer.MAX_VALUE;

    public static int[] shortestPath(int[][] graph, int startNode) {
        int n = graph.length;
        int[] dist = new int[n];
        Arrays.fill(dist, INF);
        dist[startNode] = 0;

        // PriorityQueue stores (distance, vertex)
        PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> a[0] - b[0]);
        pq.add(new int[]{0, startNode});

        while (!pq.isEmpty()) {
            int[] pair = pq.poll();
            int d = pair[0];
            int u = pair[1];

            if (d > dist[u]) continue;

            for (int v = 0; v < n; v++) {
                if (graph[u][v] != 0) { // Edge exists
                    int weight = graph[u][v];
                    if (dist[u] + weight < dist[v]) {
                        dist[v] = dist[u] + weight;
                        pq.add(new int[]{dist[v], v});
                    }
                }
            }
        }
        return dist;
    }

    public static void main(String[] args) {
        int[][] graph = { {0, 4, 0, 0, 0, 0, 0, 8, 0}, 
                          {4, 0, 8, 0, 0, 0, 0, 11, 0}, 
                          {0, 8, 0, 7, 0, 4, 0, 0, 2}, 
                          {0, 0, 7, 0, 9, 14, 0, 0, 0} };
        int[] result = shortestPath(graph, 0); 
        // Due to matrix size mismatch in the snippet, using a small array for demonstration
        System.out.println("Shortest distances from node 0 (first 4 elements): " + Arrays.toString(Arrays.copyOf(result, 4)));
    }
}

import java.lang.annotation.ElementType;
import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@interface AuditLog {
    String operation() default "UNKNOWN";
}

class Service {
    @AuditLog(operation = "CREATE_USER")
    public void createUser(String username) {
        System.out.println("User " + username + " created.");
    }
}

public class AnnotationReflectionExample {
    public static void main(String[] args) {
        Service service = new Service();
        try {
            // Use reflection to check for the annotation
            java.lang.reflect.Method method = service.getClass().getMethod("createUser", String.class);
            if (method.isAnnotationPresent(AuditLog.class)) {
                AuditLog logAnnotation = method.getAnnotation(AuditLog.class);
                System.out.println("Found AuditLog annotation with operation: " + logAnnotation.operation());
            }
            service.createUser("TyprDev");
        } catch (NoSuchMethodException e) {
            e.printStackTrace();
        }
    }
}

