#include <iostream>
#include <memory>
#include <stdexcept>

// Custom Dynamic Array (Vector) implementation for learning purposes
class DynamicArray {
private:
    std::unique_ptr<int[]> data_;
    size_t size_;
    size_t capacity_;

    void resize(size_t new_capacity) {
        std::unique_ptr<int[]> new_data = std::make_unique<int[]>(new_capacity);
        // Copy existing elements to the new array
        for (size_t i = 0; i < size_; ++i) {
            new_data[i] = data_[i];
        }
        data_ = std::move(new_data); // Transfer ownership of the new array
        capacity_ = new_capacity;
    }

public:
    DynamicArray() : data_(std::make_unique<int[]>(10)), size_(0), capacity_(10) {}

    void push_back(int val) {
        if (size_ == capacity_) {
            resize(capacity_ * 2);
        }
        data_[size_++] = val;
    }

    int at(size_t index) const {
        if (index >= size_) {
            throw std::out_of_range("Index out of bounds");
        }
        return data_[index];
    }
};

