#include <iostream>
#include <utility>

// Variadic Template for a simple type-safe printing function
template <typename T>
void print_rec(const T& t) {
    std::cout << t << std::endl;
}

template <typename T, typename... Args>
void print_rec(const T& t, const Args&... args) { // Fold expression alternative in C++17+
    std::cout << t << ", ";
    print_rec(args...);
}

// Variadic Template using fold expression (C++17+)
template <typename... Args>
void print_fold(const Args&... args) {
    // (std::cout << ... << args) would print all arguments without separators
    // This prints with space separators (less idiomatic, but works for snippet)
    ((std::cout << args << " "), ...);
    std::cout << std::endl;
}

