#include <iostream>
#include <sstream>
#include <string>

// Function to reverse the words in a sentence
std::string reverse_words(const std::string& sentence) {
    std::stringstream ss(sentence);
    std::string word;
    std::vector<std::string> words;

    // Extract words from the stringstream
    while (ss >> word) {
        words.push_back(word);
    }

    // Check if there are words to reverse
    if (words.empty()) return "";

    std::string reversed_sentence;
    // Iterate in reverse and rebuild the sentence
    for (int i = words.size() - 1; i >= 0; --i) {
        reversed_sentence += words[i];
        if (i > 0) {
            reversed_sentence += " ";
        }
    }
    return reversed_sentence;
}

