#include <iostream>
#include <algorithm>
#include <vector>

// Implementation of binary search on a sorted vector
int iterative_binary_search(const std::vector<int>& sorted_arr, int target) {
    int low = 0;
    int high = sorted_arr.size() - 1;

    while (low <= high) {
        // Calculate mid-point without risk of overflow
        int mid = low + (high - low) / 2;

        if (sorted_arr[mid] == target) {
            return mid; // Found the element
        } else if (sorted_arr[mid] < target) {
            low = mid + 1; // Search the right half
        } else {
            high = mid - 1; // Search the left half
        }
    }
    return -1; // Element not found
}

